import { AnonAadhaarPCDArgs, AnonAadhaarPCD } from 'anon-aadhaar-pcd';
import { SerializedPCD } from '@pcd/pcd-types';
import * as react_jsx_runtime from 'react/jsx-runtime';
import { ReactNode } from 'react';

/**
 * useAnonAadhaar is a custom React hook that provides access to the authentication
 * state and the login request function from the AnonAadhaarContext. This hook
 * is intended to be used within components that are descendants of the
 * AnonAadhaarProvider.
 *
 * @returns An array containing:
 *   - The authentication state (AnonAadhaarState) obtained from the context.
 *   - The login request function (startReq) obtained from the context.
 */
declare function useAnonAadhaar(): [
    AnonAadhaarState,
    (request: AnonAadhaarRequest) => void
];
type AnonAadhaarRequest = {
    type: 'login';
    args: AnonAadhaarPCDArgs;
} | {
    type: 'logout';
};
type AnonAadhaarState = {
    /** Whether the user is logged in. @see ProveButton */
    status: 'logged-out' | 'logged-in' | 'logging-in';
} & ({
    status: 'logged-out';
} | {
    status: 'logging-in';
} | {
    status: 'logged-in';
    serializedPCD: SerializedPCD<AnonAadhaarPCD>;
    pcd: AnonAadhaarPCD;
});

/**
 * AnonAadhaarProvider is a React component that serves as a provider for the
 * AnonAadhaarContext. It manages the authentication state, login requests,
 * and communication with the proving component. This provider initializes the
 * authentication state from local storage on page load and handles updates to
 * the state when login requests are made and when new proofs are received.
 *
 * @param props - Props for the AnonAadhaarProvider component.
 *   - children: The child components that will have access to the provided context.
 *
 * @returns A JSX element that wraps the provided child components with the
 * AnonAadhaarContext.Provider.
 */
declare function AnonAadhaarProvider(props: {
    children: ReactNode;
    _appId: string;
    _testing?: boolean;
    _isWeb?: boolean;
}): react_jsx_runtime.JSX.Element;

declare const AnonAadhaarProof: ({ code, label, }: {
    code: string;
    label?: string | undefined;
}) => react_jsx_runtime.JSX.Element;

/**
 * LogInWithAnonAadhaar is a React component that provides a user interface
 * for logging in and logging out using the AnonAadhaarContext. It renders a
 * button that triggers a login modal when clicked, and provides methods to
 * initiate user login using the anon aadhaar zk circuit. The component utilizes
 * the authentication state and login request function from the context.
 *
 * @returns A JSX element representing the LogInWithAnonAadhaarV2 component.
 */
declare const LogInWithAnonAadhaar: () => react_jsx_runtime.JSX.Element;

export { AnonAadhaarProof, AnonAadhaarProvider, LogInWithAnonAadhaar, useAnonAadhaar };
